const createPostTable = () => {
  const files = sync(`${CONTENT_DIR_PATH}/**/*.md*`).reverse();

  try {
    let fileAttributes = {};
    let tagsTablePublished = { blog: {}, project: {} };
    let tagsTableUnpublished = { blog: {}, project: {} };

