const NEXT_PUBLIC_CATEGORY_ID = process.env.NEXT_PUBLIC_CATEGORY_ID as string;

const Giscus = () => {
  const ref = useRef<HTMLDivElement>(null);
  const { isDarkTheme } = useTheme();

  // https://github.com/giscus/giscus/tree/main/styles/themes
